pheno.dta$EVID[pheno.dta$AMT>0] <- 101
pheno.dta$log_allo_wt <- log(pheno.dta$WT / 1.3) # 1.3 is median weight
####### Allometric clearance wihout OMEGA block to remove correlation V,CL ########
run7.mod <- function() {
  ini({
    tcl <- log(0.008) # typical value of clearance
    tv <-  log(0.6)   # typical value of volume
    all.cl <- 1 # allometric exponent on CL
    eta.cl ~ 0.1      # interindividual variability on clearance 
    eta.v ~ 0.1       # interindividual variability on volume
    #eta.cl + eta.v ~ c(1,
    #                   0.00001, 1) 
                      # interindividual variability on clearance and volume
    add.err <- 0.1    # residual variability
  })
  model({
    cl <- exp(tcl + all.cl * log_allo_wt + eta.cl) # individual value of clearance
    v <- exp(tv + eta.v)    # individual value of volume
    ke <- cl / v            # elimination rate constant
    d/dt(A1) = - ke * A1    # model differential equation
    cp = A1 / v             # concentration in plasma
    cp ~ add(add.err)       # define error model
  })
}
###### 1 CMT add err est=FO ######
run7.fo <- nlmixr(run7.mod, pheno.dta, est="fo")
#
```

